import React from 'react';
import logo from './logo.svg';
import './App.css';

import {
	IonApp,
	IonTabs,
	IonTabBar,
	IonTabButton,
	IonLabel,
	IonRouterOutlet,
	IonIcon
} from '@ionic/react';
import { IonReactRouter } from '@ionic/react-router';
import { Route } from 'react-router-dom';

import Tab1 from './pages/Tab1';

//#region CSS
/* Core CSS required for Ionic components to work properly */
import '@ionic/react/css/core.css';

/* Basic CSS for apps built with Ionic */
import '@ionic/react/css/normalize.css';
import '@ionic/react/css/structure.css';
import '@ionic/react/css/typography.css';

/* Optional CSS utils that can be commented out */
import '@ionic/react/css/padding.css';
import '@ionic/react/css/float-elements.css';
import '@ionic/react/css/text-alignment.css';
import '@ionic/react/css/text-transformation.css';
import '@ionic/react/css/flex-utils.css';
import '@ionic/react/css/display.css';
//#endregion

function App() {
  return (
      <IonApp>
        <IonReactRouter>
          <IonTabs>
              <IonRouterOutlet>
                  <Route path="/:tab(tab1)" component={Tab1} exact={true} />
                  <Route path="/:tab(tab2)" component={Tab1} exact={true} />
                  <Route path="/:tab(tab3)" component={Tab1} exact={true} />
              </IonRouterOutlet>

              <IonTabBar slot="bottom">
                  <IonTabButton tab="home" href="/tab1">
                      <IonIcon name="home" />
                      <IonLabel>Tab 1</IonLabel>
                  </IonTabButton>
                  <IonTabButton tab="settings" href="/tab2">
                      <IonIcon name="settings" />
                      <IonLabel>Tab 2</IonLabel>
                  </IonTabButton>
                  <IonTabButton tab="about" href="/tab3">
                      <IonIcon name="information-circle" />
                      <IonLabel>Tab 3</IonLabel>
                  </IonTabButton>
              </IonTabBar>
          </IonTabs>
        </IonReactRouter>
      </IonApp>
  );
}

export default App;
